INSERT INTO `wp_wc_admin_notes` VALUES (62,'custom_deposit_schedules_inbox','marketing','en_US','Customize your deposit schedule','Receive your funds according to your own business needs with custom deposit schedules from WooCommerce Payments. Youll have the flexibility to deposit funds into your bank account daily, weekly, monthly, or even on-demand  youve done the work, now get paid on your terms.','{}','unactioned','woocommerce.com','2022-09-15 09:40:54',NULL,0,'plain','',0,0,'info');
