INSERT INTO `wp_wc_admin_notes` VALUES (74,'woopay-beta-merchantrecruitment-04MAY23','info','en_US','Increase conversions with WooPay  our fastest checkout yet','WooPay, a new express checkout feature built into WooCommerce Payments, is now available and were inviting you to be one of the first to try it. \r\n<br><br>\r\nBoost conversions by offering your customers a simple, secure way to pay with a single click.\r\n<br><br>\r\nGet started in seconds.','{}','pending','woocommerce.com','2024-01-26 10:22:35',NULL,0,'plain','',0,0,'info');
